<?php
$requiem = [
    'Introitus' => 'Requiem Aeternam',
    'Kyrie' => 'Eleison',
    'Sequence' => [
        'Dies' => 'Irae',
        'Tuba' => 'Mirum',
        'Rex' => ['Tremendae', 'Majestatis'],
        'Recordare' => ['Jesu', 'Pie'],
        'Confutatis' => 'Maledictis',
        'Lacrimosa' => ['Dies', 'Illa']
    ],
    'Offertorium' => [
        'Domine' => ['Jesu', 'Christe'],
        'Versus' => ['Hostias', 'Preces']
    ],
    'Sanctus' => [
        'Sanctus' => ['Dominus' => ['Deus','Sabaoth']],
        'Benedictus' => 'Benedictus'
    ],
    'Agnus' => 'Dei',
    'Communio' => ['Lux', 'Aeterna'],
];

echo $requiem['Introitus'] . "\n";
echo $requiem['Kyrie'] . "\n";

foreach ($requiem['Sequence'] as $key => $value) {
    if (is_array($value)) {echo $key . ': ' . implode(', ', $value) . "\n";}
    else {echo $key . ': ' . $value . "\n";}
}

foreach ($requiem['Offertorium'] as $key => $value) {
    if (is_array($value)) {echo $key . ': ' . implode(', ', $value) . "\n";}
    else {echo $key . ': ' . $value . "\n";}
}

echo $requiem['Agnus'] . "\n";
echo $requiem['Communio'][0] . ' ' . $requiem['Communio'][1] . "\n";
echo "Requiem completed.\n";

?>